import { useParams, useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axios from 'axios';

export default function ExamPage() {
  const { id } = useParams();
  const [exam, setExam] = useState(null);
  const [answers, setAnswers] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`http://localhost:5000/api/exams/${id}`)
      .then(res => setExam(res.data));
  }, [id]);

  const handleSubmit = async () => {
    const user = localStorage.getItem('user') || 'Guest';
    const payload = {
      user,
      exam_id: id,
      answers: Object.entries(answers).map(([qid, option]) => ({
        question_id: "6880a75f3dd90a10443125a1",           // Not question.id or index
        selected_option: selectedIndex


      })),
    };
    const res = await axios.post('http://localhost:5000/api/results', payload);
    navigate('/result', { state: res.data });
  };

  return exam ? (
    <div>
      <h2>{exam.title}</h2>
      {exam.questions.map((q, i) => (
        <div key={q._id}>
          <p>{i + 1}. {q.question_text}</p>
          {q.options.map((opt, idx) => (
            <label key={idx}>
              <input
                type="radio"
                name={q._id}
                value={idx}
                onChange={() => setAnswers({ ...answers, [q._id]: idx })}
              />
              {opt}
            </label>
          ))}
        </div>
      ))}
      <button onClick={handleSubmit}>Submit</button>
    </div>
  ) : <p>Loading...</p>;
}
