import { useParams, useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axios from 'axios';

export default function ExamPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [exam, setExam] = useState(null);
  const [answers, setAnswers] = useState({});

  // Fetch the exam by ID
  useEffect(() => {
    axios.get(`http://localhost:5000/api/exams/${id}`)
      .then((res) => setExam(res.data))
      .catch((err) => console.error("Error fetching exam:", err));
  }, [id]);

  // Handle answer selection
  const handleOptionChange = (question_id, selected_option) => {
    setAnswers((prev) => ({
      ...prev,
      [question_id]: selected_option
    }));
  };

  // Handle submit
  const handleSubmit = async () => {
    if (!exam) return;

    const payload = {
      user: 'Anonymous',
      exam_id: exam._id,
      answers: exam.questions.map((q) => ({
        question_id: q.question_id, // ✅ use question_id
        selected_option: answers[q.question_id] ?? -1
      }))
    };

    try {
      const res = await axios.post('http://localhost:5000/api/results', payload);
      console.log("Submitted Payload:", payload);
      navigate('/result', { state: { score: res.data.score } });
    } catch (err) {
      console.error("Submit error:", err);
      alert("Something went wrong. Please try again.");
    }
  };

  return exam ? (
    <div style={{ padding: '20px' }}>
      <h2>{exam.title}</h2>
      <p>{exam.description}</p>

      {exam.questions.map((q, index) => (
        <div key={q.question_id} style={{ marginBottom: '20px' }}>
          <p><strong>{index + 1}. {q.question_text}</strong></p>
          {q.options.map((option, idx) => (
            <label key={idx} style={{ display: 'block', margin: '5px 0' }}>
              <input
                type="radio"
                name={q.question_id}
                value={idx}
                checked={answers[q.question_id] === idx}
                onChange={() => handleOptionChange(q.question_id, idx)}
              />
              {' '}
              {option}
            </label>
          ))}
        </div>
      ))}

      <button onClick={handleSubmit} style={{ marginTop: '20px' }}>
        Submit
      </button>
    </div>
  ) : <p>Loading exam...</p>;
}
