import { useParams, useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axios from 'axios';

export default function ExamPage() {
  const { id } = useParams();
  const [exam, setExam] = useState(null);
  const [answers, setAnswers] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    axios.get(`http://localhost:5000/api/exams/${id}`)
      .then(res => setExam(res.data));
  }, [id]);

  const handleSubmit = async () => {
    const payload = {
      user: 'Anonymous',
      exam_id: exam._id,
      answers: exam.questions.map((q) => ({
        question_id: q.question_id,
        selected_option: answers[q._id] ?? -1 // default to -1 if not answered
      }))
    };

    try {
      const res = await axios.post('http://localhost:5000/api/results', payload);
      console.log(payload)
      navigate('/result', { state: { score: res.data.score } });
    } catch (err) {
      console.error('Submit error:', err);
    }
  };

  return exam ? (
    <div>
      <h2>{exam.title}</h2>
      {exam.questions.map((q, i) => (
        <div key={q._id}>
          <p>{i + 1}. {q.question_text}</p>
          {q.options.map((opt, idx) => (
            <label key={idx}>
              <input
                type="radio"
                name={q._id}
                value={idx}
                checked={answers[q._id] === idx}
                onChange={() => setAnswers({ ...answers, [q._id]: idx })}
              />
              {opt}
            </label>
          ))}
        </div>
      ))}
      <button onClick={handleSubmit}>Submit</button>
    </div>
  ) : <p>Loading...</p>;
}
