import { useParams, useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axios from 'axios';

export default function ExamPage() {
  const { id } = useParams();
  const [exam, setExam] = useState(null);
  const [answers, setAnswers] = useState({});
  const navigate = useNavigate();

  // Fetch the exam by ID from backend
  useEffect(() => {
    axios.get(`http://localhost:5000/api/exams/${id}`)
      .then(res => setExam(res.data));
  }, [id]);

  // When user submits the exam
  const handleSubmit = async () => {
    const payload = {
      user: 'Anonymous', // You can change this to a real user if needed
      exam_id: exam._id,
      answers: exam.questions.map(q => ({
        question_id: q._id.toString(), // ✅ Important: convert ObjectId to string
        selected_option: answers[q._id.toString()] ?? -1 // ✅ Ensure proper mapping
      }))
    };

    try {
      const res = await axios.post('http://localhost:5000/api/results', payload);
      navigate('/result', { state: { score: res.data.score } });
    } catch (err) {
      console.error('❌ Submit error:', err);
    }
  };

  return exam ? (
    <div>
      <h2>{exam.title}</h2>
      {exam.questions.map((q, i) => (
        <div key={q._id}>
          <p>{i + 1}. {q.question_text}</p>
          {q.options.map((opt, idx) => (
            <label key={idx} style={{ display: 'block' }}>
              <input
                type="radio"
                name={q._id}
                value={idx}
                onChange={() =>
                  setAnswers({ ...answers, [q._id.toString()]: idx }) // ✅ Use string key
                }
              />
              {opt}
            </label>
          ))}
        </div>
      ))}
      <button onClick={handleSubmit}>Submit</button>
    </div>
  ) : <p>Loading...</p>;
}
