import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function LoginPage() {
  const [username, setUsername] = useState('');
  const navigate = useNavigate();

  const handleLogin = () => {
    if (username.trim()) {
      localStorage.setItem('user', username);
      navigate('/exams');
    }
  };

  return (
    <div
      style={{
        position: 'relative',
        backgroundImage: `url('https://images.unsplash.com/photo-1519389950473-47ba0277781c?auto=format&fit=crop&w=1470&q=80')`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        height: '100vh',
        fontFamily: 'Arial, sans-serif',
      }}
    >
      {/* ðŸ”† White semi-transparent overlay */}
      <div
        style={{
          position: 'absolute',
          top: 0,
          bottom: 0,
          left: 0,
          right: 0,
          backgroundColor: 'rgba(255, 255, 255, 0.6)', // lighter overlay
          zIndex: 1,
        }}
      ></div>

      {/* ðŸ‘‡ Login card content */}
      <div
        style={{
          position: 'relative',
          zIndex: 2,
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          height: '100%',
        }}
      >
        <div
          style={{
            backgroundColor: 'white',
            padding: '40px',
            borderRadius: '12px',
            boxShadow: '0 8px 24px rgba(0,0,0,0.15)',
            width: '340px',
            textAlign: 'center',
          }}
        >
          <h2 style={{ marginBottom: '20px', color: '#333' }}>Online MCQ Exam</h2>
          <input
            placeholder="Enter your name"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            style={{
              padding: '10px',
              width: '100%',
              marginBottom: '20px',
              borderRadius: '8px',
              border: '1px solid #ccc',
              fontSize: '16px',
            }}
          />
          <button
            onClick={handleLogin}
            style={{
              backgroundColor: '#2d89ef',
              color: 'white',
              padding: '10px 20px',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer',
              fontSize: '16px',
              width: '100%',
            }}
          >
            Login
          </button>
          <p>Don't have an account? <a href="/signup">Sign up here</a></p>

        </div>
      </div>
    </div>
  );
}
